Scripts.Script:

function logEvent(event) {
    var level = event.level;
    var msg = event.message.getFormattedMessage();
    switch (level) {
        case "INFO":
            return "\u001B[32m" + msg + "\u001B[0m"; // Green
        case "PASS":
            return "\u001B[33m" + msg + "\u001B[0m"; // Yellow
        case "FAIL":
            return "\u001B[31m" + msg + "\u001B[0m"; // Red
        default:
            return msg;
    }
}
